import React, { Component } from "react";
// import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, FormBtn } from "../components/Form";

class Saved extends Component {
  state = {
    books: [],
    query: ""
  };
  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = () => {
    API.getAllSavedBooks()
      .then(res =>
        this.setState({ books: res})
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };
  render() {
    console.log("this search saved", this.state)
    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>What Books Should I Read?</h1>
            </Jumbotron>
            <form>
              <Input
                value={this.state.query}
                onChange={this.handleInputChange}
                name="query"
                placeholder="Search Value (required)"
              />
              <FormBtn
                disabled={!(this.state.query)}
                onClick={this.handleFormSubmit}
              >
                Search
              </FormBtn>
            </form>
          </Col>
          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>Books On My List</h1>
            </Jumbotron>
            {this.state.books.length ? (
              <List>
                {this.state.books.map((book, i) => (
                  <ListItem key={`googlebook-${i}`}>
                    <h4>{book.volumeInfo.title}</h4>
                    <img href={book.volumeInfo.imageLinks.thumbnail} alt={book.volumeInfo.title}/>
                    <p>{book.volumeInfo.description}</p>
                    <a href={book.volumeInfo.selfLink}>View book</a>
                    {/* <button onClick={()=>(this.saveBook(i))}>save</button> */}
                   {/* 
                    title
                    authors
                    description
                    image
                    link â€“ view
                    save button
                  */}
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Saved;
